import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import HomePage from './components/HomePage';
import ProductList from './components/ProductList';
import ProductForm from './components/ProductForm';

const App = () => {
    return (
        <Router>
            <div>
                <Switch>
                    <Route path="/" exact component={HomePage} />
                    <Route path="/products" exact component={ProductList} />
                    <Route path="/add-product" exact>
                        <ProductForm onSuccess={() => window.location.href = '/products'} onCancel={() => window.location.href = '/products'} />
                    </Route>
                    <Route path="/edit-product/:id" exact>
                        {({ match }) => (
                            <ProductForm productId={match.params.id} onSuccess={() => window.location.href = '/products'} onCancel={() => window.location.href = '/products'} />
                        )}
                    </Route>
                </Switch>
            </div>
        </Router>
    );
};

export default App;
