import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const ClientList = () => {
    const [clients, setClients] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5001/api/clients')
            .then(response => setClients(response.data))
            .catch(error => console.error('Error al obtener los clientes:', error));
    }, []);

    const deleteClient = (id) => {
        axios.delete(`http://localhost:5001/api/clients/${id}`)
            .then(() => {
                setClients(clients.filter(client => client._id !== id));
            })
            .catch(error => console.error('Error al eliminar el cliente:', error));
    };

    return (
        <div className="container mx-auto">
            <h1 className="text-3xl font-bold mb-4">Lista de Clientes</h1>
            <Link to="/add-client" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
                Agregar Cliente
            </Link>
            <ul className="mt-4">
                {clients.map(client => (
                    <li key={client._id} className="mb-2">
                        {client.name} {client.apellido}
                        <button
                            onClick={() => deleteClient(client._id)}
                            className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 ml-4"
                        >
                            Eliminar
                        </button>
                        <Link to={`/edit-client/${client._id}`} className="ml-4 bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600">
                            Editar
                        </Link>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ClientList;
