import React, { useState, useEffect } from 'react';
import { getProducts, deleteProduct } from '../services/productService';
import { Link } from 'react-router-dom';


const ProductList = ({ onEdit }) => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await getProducts();
                setProducts(response.data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchProducts();
    }, []);

    const handleDelete = async (id) => {
        try {
            await deleteProduct(id);
            setProducts(products.filter(product => product._id !== id));
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    return (
        <div>
            <h2>Lista de Productos</h2>
            <Link to="/add-product" style={{ display: 'block', marginBottom: '20px', textDecoration: 'none', color: 'blue' }}>
                Agregar Nuevo Producto
            </Link>
            <ul>
                {products.map(product => (
                    <li key={product._id}>
                        {product.name} - ${product.price}
                        <Link to={`/edit-product/${product._id}`} style={{ marginLeft: '10px', textDecoration: 'none', color: 'green' }}>
                            Editar
                        </Link>
                        <button onClick={() => handleDelete(product._id)} style={{ marginLeft: '10px' }}>
                            Eliminar
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ProductList;
