import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { createVenta, updateVenta, getVentaById } from '../services/ventaService';
import { getClients } from '../services/clientService'; // Asumimos que tambiÃ©n necesitas obtener clientes

const VentaForm = () => {
    const [venta, setVenta] = useState({ clientId: '', date: '', detalles: [] });
    const [clients, setClients] = useState([]);
    const navigate = useNavigate();
    const { id } = useParams();

    useEffect(() => {
        if (id) {
            const fetchVenta = async () => {
                try {
                    const response = await getVentaById(id);
                    setVenta(response.data);
                } catch (error) {
                    console.error('Error fetching venta:', error);
                }
            };

            fetchVenta();
        }

        const fetchClients = async () => {
            try {
                const response = await getClients();
                setClients(response.data);
            } catch (error) {
                console.error('Error fetching clients:', error);
            }
        };

        fetchClients();
    }, [id]);

    const handleChange = (e) => {
        setVenta({ ...venta, [e.target.name]: e.target.value });
    };

    const handleDetalleChange = (index, e) => {
        const newDetalles = [...venta.detalles];
        newDetalles[index][e.target.name] = e.target.value;
        setVenta({ ...venta, detalles: newDetalles });
    };

    const handleAddDetalle = () => {
        setVenta({ ...venta, detalles: [...venta.detalles, { productId: '', quantity: '', price: '' }] });
    };

    const handleRemoveDetalle = (index) => {
        const newDetalles = [...venta.detalles];
        newDetalles.splice(index, 1);
        setVenta({ ...venta, detalles: newDetalles });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (id) {
                await updateVenta(id, venta);
            } else {
                await createVenta(venta);
            }
            navigate('/ventas'); // Redirigir a la lista de ventas despuÃ©s de guardar
        } catch (error) {
            console.error('Error saving venta:', error);
        }
    };

    return (
        <div className="max-w-4xl mx-auto mt-10 bg-white p-8 shadow-md rounded">
            <h2 className="text-2xl font-bold mb-6">{id ? 'Editar Venta' : 'Agregar Venta'}</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <select
                    name="clientId"
                    value={venta.clientId}
                    onChange={handleChange}
                    className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                >
                    <option value="">Seleccionar Cliente</option>
                    {clients.map(client => (
                        <option key={client._id} value={client._id}>{client.name}</option>
                    ))}
                </select>
                <input
                    type="date"
                    name="date"
                    value={venta.date ? new Date(venta.date).toISOString().split('T')[0] : ''}
                    onChange={handleChange}
                    className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                />
                {venta.detalles.map((detalle, index) => (
                    <div key={index} className="flex space-x-4 mb-4">
                        <input
                            type="text"
                            name="productId"
                            value={detalle.productId}
                            onChange={e => handleDetalleChange(index, e)}
                            placeholder="ID del Producto"
                            className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                            required
                        />
                        <input
                            type="number"
                            name="quantity"
                            value={detalle.quantity}
                            onChange={e => handleDetalleChange(index, e)}
                            placeholder="Cantidad"
                            className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                            required
                        />
                        <input
                            type="number"
                            name="price"
                            value={detalle.price}
                            onChange={e => handleDetalleChange(index, e)}
                            placeholder="Precio"
                            className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                            required
                        />
                        <button
                            type="button"
                            onClick={() => handleRemoveDetalle(index)}
                            className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                        >
                            Eliminar
                        </button>
                    </div>
                ))}
                <button
                    type="button"
                    onClick={handleAddDetalle}
                    className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                >
                    Agregar Detalle
                </button>
                <button
                    type="submit"
                    className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
                >
                    {id ? 'Actualizar Venta' : 'Crear Venta'}
                </button>
            </form>
        </div>
    );
};

export default VentaForm;
