import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const ClientList = () => {
    const [clients, setClients] = useState([]);

    useEffect(() => {
        const fetchClients = async () => {
            try {
                const response = await fetch('http://localhost:5001/api/clients');
                const data = await response.json();
                setClients(data);
            } catch (error) {
                console.error('Error al obtener los clientes:', error);
            }
        };
        fetchClients();
    }, []);

    const handleDelete = async (id) => {
        try {
            await fetch(`http://localhost:5001/api/clients/${id}`, { method: 'DELETE' });
            setClients(clients.filter(client => client._id !== id));
        } catch (error) {
            console.error('Error al eliminar el cliente:', error);
        }
    };

    return (
        <div className="max-w-4xl mx-auto mt-10">
            <h1 className="text-3xl font-bold mb-6 text-gray-800">Lista de Clientes</h1>
            <div className="bg-white shadow-md rounded-lg p-6">
                {clients.length > 0 ? (
                    <ul>
                        {clients.map(client => (
                            <li key={client._id} className="border-b py-4">
                                <h2 className="text-xl font-semibold">{client.name} {client.apellido}</h2>
                                <p className="text-gray-600">Email: {client.email}</p>
                                <p className="text-gray-600">Tel√©fono: {client.phone || 'No especificado'}</p>
                                <div className="mt-2 space-x-4">
                                    <Link to={`/edit-client/${client._id}`} className="text-blue-500 hover:underline">Editar</Link>
                                    <button onClick={() => handleDelete(client._id)} className="text-red-500 hover:underline">Eliminar</button>
                                </div>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p className="text-gray-600">No hay clientes disponibles.</p>
                )}
            </div>
        </div>
    );
};

export default ClientList;
