import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const ClientForm = () => {
  const [client, setClient] = useState({ name: '', apellido: '', email: '', phone: '', address: '' });
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    if (id) {
      axios.get(`http://localhost:5001/api/clients/${id}`)
        .then(response => setClient(response.data))
        .catch(error => console.error('Error al obtener el cliente:', error));
    }
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (id) {
      axios.put(`http://localhost:5001/api/clients/${id}`, client)
        .then(() => navigate('/clients'))
        .catch(error => console.error('Error al actualizar el cliente:', error));
    } else {
      axios.post('http://localhost:5001/api/clients', client)
        .then(() => navigate('/clients'))
        .catch(error => console.error('Error al crear el cliente:', error));
    }
  };

  return (
    <div className="container mx-auto">
      <h1 className="text-3xl font-bold mb-4">{id ? 'Editar Cliente' : 'Agregar Cliente'}</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={client.name}
          onChange={e => setClient({ ...client, name: e.target.value })}
          placeholder="Nombre"
          className="border px-2 py-1 mb-4"
          required
        />
        <input
          type="text"
          value={client.apellido}
          onChange={e => setClient({ ...client, apellido: e.target.value })}
          placeholder="Apellido"
          className="border px-2 py-1 mb-4"
          required
        />
        <input
          type="email"
          value={client.email}
          onChange={e => setClient({ ...client, email: e.target.value })}
          placeholder="Email"
          className="border px-2 py-1 mb-4"
          required
        />
        <input
          type="text"
          value={client.phone}
          onChange={e => setClient({ ...client, phone: e.target.value })}
          placeholder="Teléfono"
          className="border px-2 py-1 mb-4"
        />
        <input
          type="text"
          value={client.address}
          onChange={e => setClient({ ...client, address: e.target.value })}
          placeholder="Dirección"
          className="border px-2 py-1 mb-4"
        />
        <button
          type="submit"
          className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
        >
          {id ? 'Actualizar' : 'Agregar'}
        </button>
      </form>
    </div>
  );
};

export default ClientForm;
