import React, { useState, useEffect } from 'react';
import { getProducts, deleteProduct } from '../services/productService';
import { Link } from 'react-router-dom';


const ProductList = ({ onEdit }) => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await getProducts();
                setProducts(response.data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchProducts();
    }, []);

    const handleDelete = async (id) => {
        try {
            await deleteProduct(id);
            setProducts(products.filter(product => product._id !== id));
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    return (
        <div className="max-w-4xl mx-auto mt-10">
            <h1 className="text-3xl font-bold mb-6 text-gray-800">Lista de Productos</h1>
            <div className="bg-white shadow-md rounded-lg p-6">
                {products.length > 0 ? (
                    <ul>
                        {products.map(product => (
                            <li key={product._id} className="border-b py-4">
                                <h2 className="text-xl font-semibold">{product.name}</h2>
                                <p className="text-gray-600">Precio: ${product.price}</p>
                                <p className="text-gray-600">Stock: {product.stock}</p>
                                <div className="mt-2 space-x-4">
                                    <Link to={`/edit-product/${product._id}`} className="text-blue-500 hover:underline">Editar</Link>
                                    <button onClick={() => handleDelete(product._id)} className="text-red-500 hover:underline">Eliminar</button>
                                </div>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p className="text-gray-600">No hay productos disponibles.</p>
                )}
            </div>
        </div>
    );
};

export default ProductList;
